import React from 'react'
import {withFormik, Form, Field} from 'formik'
import * as Yup from 'yup'

const App = ({
  values,
  errors,
  touched
}) => (
  <Form>

    <div>
      <Field 
        name="name"
        placeholder="Your Name"/>
        <br/>
    </div>

    <div> {touched.email && errors.email && <p> {errors.email}</p>}
      <Field
        type="email" 
        name="email" 
        placeholder="Your Email"/>
        <br/>
    </div>

    <div>{touched.password && errors.password && <p> {errors.password}</p>}
    <Field
      type="password"
      name="password"
      placeholder="Your Password"/>
      <br/>
    </div>

    <button type="submit">Submit</button>
  </Form>
)


const Registration = withFormik ({
  mapPropsToValues({name, email, password}) {
    return {
      email: email || '',
      password: password ||'',
      name: name || ''
    }
  },

  validationSchema: Yup.object().shape({
    email: Yup.string()
              .email('Email not valid')
              .max(100)
              .required('Email is required'),
    password: Yup.string()
                 .min(8, 'Password must be 8 characters or longer')
                 .max(100, 'Password must be shorter than 200 characters')
                //  .matches('^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!.%*?&])[A-Za-z\d@$!%*?&]', 
                //             'Password must have a number, capital letter and a special character.')
                 .required('Password is required')
  }),
  handleSubmit(values) {
    let headers = new Headers();
    // headers.append('Content-Type', 'application/json');
    // headers.append('Accept', 'application/json');
    // headers.append('Access-Control-Allow-Origin', 'http://localhost:3000');
    // headers.append('Access-Control-Allow-Credentials', 'true');
    // headers.append('POST', 'OPTIONS');

    (async () => {
        const rawResponse = await fetch('http://localhost:5000/api/user/register', {
          method: 'POST',
        //   mode: 'cors',
        //   headers: headers,
          contentType: "application/json",
          body: JSON.stringify({"name": values.name, "email": values.email, "password": values.password})
        });
        const content = await rawResponse.json();

        console.log(content);
      })();
  }
}) (App)

export default Registration
