import React from 'react'
import { render } from 'react-dom'
import {withFormik, Form, Field} from 'formik'
import * as Yup from 'yup'

const App = ({
  values,
  errors,
  touched
}) => (
  <Form>

    <div>
      <Field name="name" placeholder="Your Name" />
    </div>

    <div> {touched.email && errors.email && <p> {errors.email}</p>}
      <Field
        type="email" 
        name="email" 
        placeholder="Your Email" 
        /><br/>
    </div>

    <div>{touched.password && errors.password && <p> {errors.password}</p>}
    <Field
      type="password"
      name="password"
      placeholder="Your Password"/>
      <br/>
    </div>

    <button>Submit</button>
  </Form>
)


const FormikApp = withFormik ({
  mapPropsToValues({email, password, newsletter, plan}) {
    return {
      email: email || '',
      password: password ||'',
      newsletter: newsletter || true,
      plan: plan || 'free'
    }
  },

  validationSchema: Yup.object().shape({
    email: Yup.string().email('Email not valid').required('Email is required'),
    password: Yup.string().min(6, 'Password must be 6 characters or longer').required('Password is required')
  }),
  handleSubmit(values) {
    setTimeout(() => {
      if(values.email === 'test@test.com') {

      } else {

      }
    })
    console.log(values)
  }
}) (App)


render(<FormikApp />, document.getElementById('root'));
