import React from 'react'
import {withFormik, Form, Field} from 'formik'
import * as Yup from 'yup'

const App = ({
  values,
  errors,
  touched
}) => (
  <Form>

    <div>
      <Field 
        name="name"
        placeholder="Your Name"/>
        <br/>
    </div>

    <div> {touched.email && errors.email && <p> {errors.email}</p>}
      <Field
        type="email" 
        name="email" 
        placeholder="Your Email"/>
        <br/>
    </div>

    <div>{touched.password && errors.password && <p> {errors.password}</p>}
    <Field
      type="password"
      name="password"
      placeholder="Your Password"/>
      <br/>
    </div>

    <button type="submit">Submit</button>
  </Form>
)


const Registration = withFormik ({
  mapPropsToValues({name, email, password}) {
    return {
      email: email || '',
      password: password ||'',
      name: name || ''
    }
  },

  validationSchema: Yup.object().shape({
    email: Yup.string().email('Email not valid').required('Email is required'),
    password: Yup.string().min(8, 'Password must be 8 characters or longer').required('Password is required')
  }),
